mapper.delete:
delete from ms_zffp
where mzxh = #{mzxh,jdbcType=NUMERIC}
  and jgid = #{jgid,jdbcType=NUMERIC}

mapper.select:
select 'true' as QUERYID,mzxh, jgid, fphm, czgh, jzrq, mzlb, hzrq, zfrq, zfyy, jzlsh, 
ddbh, ddtksj, ref_traceno, t_ref_ordno, state
from ms_zffp
where mzxh = #{mzxh,jdbcType=NUMERIC}
  and jgid = #{jgid,jdbcType=NUMERIC}
