mapper.delete:
delete from yf_kcmx
where sbxh = #{sbxh,jdbcType=NUMERIC}
  and jgid = #{jgid,jdbcType=NUMERIC}

mapper.select:
select 'true' as QUERYID,sbxh, jgid, yfsb, ckbh, ypxh, ypcd, ypph, ypxq, ypsl, jybz, 
lsjg, pfjg, jhjg, lsje, pfje, jhje, ykjh, yklj, ykjj, ykpj, ywsj, ywlx, ykkcsb, kcly, 
jyyy, state
from yf_kcmx
where sbxh = #{sbxh,jdbcType=NUMERIC}
  and jgid = #{jgid,jdbcType=NUMERIC}
